class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-btn",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((o=>{o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:o=>{console.log(o);let n=t.newCommentDom(o.data.comment);$(`#post-comments-${e}`).prepend(n),t.deleteComment($(" .delete-comment-btn",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n                      <p>\n                          \n                          <small>\n                              <a class="delete-comment-btn" href="/comments/destroy/${e._id}">X</a>\n                          </small>\n                          \n                          ${e.content}\n                          <br>\n                          <small>\n                              ${e.user.name}\n                          </small>\n                          <small>\n                            \n                          <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n                              0 Likes\n                          </a>\n                      \n                      </small>\n\n                      </p>    \n\n              </li>`)}deleteComment(e){$(e).click((t=>{t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:e=>{$(`#comment-${e.data.comment_id}`).remove(),console.log(e),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))}}console.log("hello world");